<?php get_header(); ?><div class="blog">    <div class="container">    <div class="row">        <div class="col-12">             <?php            $paged = (get_query_var('paged')) ? get_query_var('paged') : 1;            $showposts = get_option( 'posts_per_page' );            $args = array(                'post_type' => array( 'post', 'matches', 'team', 'page', 'tournament'),                'posts_per_page' => $showposts,                'paged' => $paged,                 's' => get_search_query(),            );            $new_query = new WP_Query($args );            ?>            <?php if ( $new_query->have_posts() ) : ?>                <?php /* Start the Loop */ ?>                <?php while ( $new_query->have_posts() ) : $new_query->the_post(); ?>         <div class="blog-content wcontainer psearch-content">                    <h2><a href="<?php the_permalink(); ?>"> <?php the_title(); ?> </a></h2>                   <?php echo esc_attr(get_post_type( get_the_ID() )); ?>         </div>        <!-- /.blog-post -->            <?php endwhile; ?>            <?php else : ?>               <div class="psearch-content">                    <h4 class="entry-title"><?php esc_html_e( 'Nothing Found', 'arcane' ); ?></h4>                    <p><?php esc_html_e( 'Sorry, but nothing matched your search criteria. Please try again with some different keywords.', 'arcane' ); ?></p>                </div><!-- .entry-content -->            <?php endif; ?>              <div class="pagination">            <ul>              <li>                <?php            $additional_loop = new WP_Query('showposts='.$showposts.'&paged='.$paged.'&s='.get_search_query().'&post_type=array(post,page,team,matches, tournament)' );            $page=$additional_loop->max_num_pages;            echo arcane_kriesi_pagination($additional_loop->max_num_pages);            ?>            <?php wp_reset_postdata(); ?>              </li>            </ul>         </div>          <div class="clear"></div>           </div>   </div>       </div></div><?php get_footer(); ?>