            <div class="comment-form"><?php    $req = get_option('require_name_email');    if ( ! empty($post->post_password) ) :        if ( $_COOKIE['wp-postpass_' . COOKIEHASH] != $post->post_password ) :?>                <div class="nopassword"><?php esc_html_e('This post is password protected. Enter the password to view any comments.', 'arcane') ?></div>            </div><?php        return;    endif;endif;?><?php if ( have_comments() ) : ?>	<?php$allowed_tags = array(	'span' => array(		'class' => array()	),	'a' => array(		'href' => array(),		'title' => array()	),	'em' => array());?><?php$ping_count = $comment_count = 0;foreach ( $comments as $comment )    get_comment_type() == "comment" ? ++$comment_count : ++$ping_count;?><?php if ( ! empty($comments_by_type['comment']) ) : ?>                <div id="comments-list" class="comments">                    <h4><?php printf($comment_count > 1 ? wp_kses(__('<span>%d</span> Comments', 'arcane'), $allowed_tags ) : wp_kses(__('<span>One</span> Comment', 'arcane'), $allowed_tags ), $comment_count) ?></h4><?php $total_pages = get_comment_pages_count(); if ( $total_pages > 1 ) : ?>                    <div id="comments-nav-above" class="comments-navigation">                                <div class="paginated-comments-links"><?php paginate_comments_links(); ?></div>                    </div><?php endif; ?>                    <ol><?php wp_list_comments('type=comment&callback=arcane_custom_comments'); ?>                    </ol><?php $total_pages = get_comment_pages_count(); if ( $total_pages > 1 ) : ?>                <div id="comments-nav-below" class="comments-navigation">                        <div class="paginated-comments-links"><?php paginate_comments_links(); ?></div>            </div><?php endif; ?>                </div><?php endif;  ?><?php if ( ! empty($comments_by_type['pings']) ) : ?>                <div id="trackbacks-list" class="comments">                    <h4><?php printf($ping_count > 1 ? wp_kses(__('<span>%d</span> Trackbacks', 'arcane'), $allowed_tags ) : wp_kses(__('<span>One</span> Trackback', 'arcane'), $allowed_tags ), $ping_count) ?></h4>                    <ol><?php wp_list_comments('type=pings&callback=arcane_custom_pings'); ?>                    </ol>                </div><?php endif  ?><?php endif ?><?php if ( 'open' == $post->comment_status ) : ?>                <div id="respond_comments">                    <h4><?php comment_form_title( esc_html__('Leave a comment', 'arcane'), esc_html__('Post a Reply to %s', 'arcane') ); ?></h4>                    <div id="cancel-comment-reply"><?php cancel_comment_reply_link() ?></div><?php if ( get_option('comment_registration')) : ?>                    <p id="login-req">                        <?php echo esc_html__('You must be ', 'arcane').'<a href="'.get_option('siteurl') . '/wp-login.php?redirect_to=' . get_permalink().'" title="Log in">'.esc_html__('logged in', 'arcane').'</a>'.esc_html__(' to post a comment.', 'arcane'); ?>                    </p><?php else : ?>                    <div class="formcontainer">                        <form id="commentform" action="<?php echo esc_url(get_option('siteurl')); ?>/wp-comments-post.php" method="post">                            <p id="comment-notes"><?php wp_kses(_e('Your email is <em>never</em> published nor shared.', 'arcane'), $allowed_tags ); ?> <?php if ($req) wp_kses(_e('Required fields are marked <span class="required">*</span>', 'arcane'), $allowed_tags ); ?></p>              <div id="form-section-author" class="form-section"><div class="form-label"><label for="author"><?php esc_html_e('Name', 'arcane') ?> <?php if ($req) wp_kses(_e('<span class="required">*</span>', 'arcane'), $allowed_tags ); ?></label></div>                                <div class="form-input"><input id="author" name="author" type="text" value="" size="30" maxlength="20" tabindex="3" /></div>              </div>              <div id="form-section-email" class="form-section">                               <div class="form-label"><label for="email"><?php esc_html_e('Email', 'arcane') ?> <?php if ($req) wp_kses(_e('<span class="required">*</span>', 'arcane'), $allowed_tags ); ?></label></div>                                <div class="form-input"><input id="email" name="email" type="text" value="" size="30" maxlength="50" tabindex="4" /></div>              </div>              <div id="form-section-url" class="form-section">                                <div class="form-label"><label for="url"><?php esc_html_e('Website', 'arcane') ?></label></div>                                <div class="form-input"><input id="url" name="url" type="text" value="" size="30" maxlength="50" tabindex="5" /></div>              </div>              <div id="form-section-comment" class="form-section">                                <div class="form-textarea">                                    <div class="form-label"><label for="author"><?php esc_html_e('Comment', 'arcane') ?></label></div>                                    <textarea id="comment" name="comment" cols="45" rows="8" tabindex="6"></textarea></div>              </div>              <div id="form-section-consent" class="form-section input-prepend">                  <input id="wp-comment-cookies-consent" name="wp-comment-cookies-consent" type="checkbox" value="yes"  />                  <?php esc_html_e( 'Save my name, email, and website in this browser for the next time I comment.', 'arcane'); ?>              </div><!-- #form-section-comment .form-section --><?php do_action('comment_form', $post->ID); ?>                            <div class="form-submit"><input id="submit" name="submit"  class="button-small button-green" type="submit" value="<?php esc_html_e('Post', 'arcane') ?>" tabindex="7" /><input type="hidden" name="comment_post_ID" value="<?php echo esc_attr($id); ?>" /></div><?php comment_id_fields(); ?>                        </form>                    </div><?php endif  ?>                </div><?php endif  ?>            </div>