<?php/** * The Template for displaying all single posts. * * @package WordPress */?><?php get_header();?><!-- Page content    ================================================== --><!-- Wrap the rest of the page in another container to center all the content. --><?php$template = '';$options = arcane_get_theme_options();if(isset($options['posts_template']))$template = $options['posts_template'];?><div class=" blog blog-ind">	<div class="container ">	<div class="row">            <?php if($template == 'left'){ ?>                <div class="col-4  ">                    <?php require_once (get_theme_file_path('sidebar.php')); ?>                </div><!-- /.span4 -->            <?php } ?>            <?php if($template != 'full'){ ?>			<div class="col-8 ">		    <?php } ?>				<?php if ( have_posts() ) : while ( have_posts() ) : the_post(); ?>					<?php require_once (get_theme_file_path('blog-single.php')); ?>				<?php endwhile; endif;wp_reset_postdata(); ?>				<div class="clear"></div>				<div class="pagination">						<?php						$args = array(			                'before'           => '<ul class="wp_pag">',			                'after'            => '</ul>',			                'link_before'      => '<li>',			                'link_after'       => '</li>',			                'next_or_number'   => 'next_and_number',			                'separator'        => '',			                'nextpagelink'     => '&raquo;',			                'previouspagelink' => '&laquo;',			                'pagelink'         => '%',			                'echo'             => 1        				);        				wp_link_pages($args); ?>				</div>                <?php comments_template(); ?>            <?php if($template != 'full'){ ?>			</div><!-- /.span8 -->			 <?php } ?>            <?php if($template == 'right'){ ?>    			<div class="col-4  ">    				<?php require_once (get_theme_file_path('sidebar.php')); ?>    			</div><!-- /.span4 -->			<?php } ?>	</div><!-- /row -->	</div><!-- /container --></div><!-- /blog --><?php get_footer(); ?>