<?php get_header(); ?><?php$options = arcane_get_theme_options();if(!isset($options['singleprod']))$options['singleprod'] = 'right-sidebar';if(!isset($options['mainshop']))$options['mainshop'] = 'right-sidebar';if (!function_exists('arcane_loop_columns')) {    function arcane_loop_columns() {        return 3; // 3 products per row    }}?><div class="page normal-page container-wrap">      <div class="container">		<div class="row">		    <?php            if ( $options['mainshop'] ) {                 if($options['mainshop'] == 'left-sidebar'){                    $main_shop_layout = 's2';                 }elseif($options['mainshop'] == 'right-sidebar'){                    $main_shop_layout = 's3';                 }else{                    $main_shop_layout = 's1';                 }            }else{               $main_shop_layout = 's1';            }            if ( $options['singleprod'] ) {	            if($options['singleprod'] == 'left-sidebar'){		            $single_product_layout = 's2';	            }elseif($options['singleprod'] == 'right-sidebar'){		            $single_product_layout = 's3';	            }else{		            $single_product_layout = 's1';	            }            }else{               $single_product_layout = 's1';            }            if(is_product()){                if($single_product_layout == 's2' || $single_product_layout == 's3'){                    add_filter('loop_shop_columns', 'arcane_loop_columns');                }                switch($single_product_layout) {                    case 's1': ?>                      <div class="col-12">                        <?php woocommerce_content(); ?>                      <div class="clear"></div>                    <?php  break;                    case 's3':                        echo '<div class="col-8">';                            woocommerce_content();                        echo '</div><!--/col-8-->';                        echo '<div id="sidebar" class="col-4">';                            get_sidebar();                        echo '</div><!--/col-8-->';                        break;                    case 's2':                        echo '<div id="sidebar" class="col-4">';                            get_sidebar();                        echo '</div><!--/col-8-->';                        echo '<div class="col-8">';                            woocommerce_content();                        echo '</div><!--/col-8-->';                        break;                    default:                        woocommerce_content();                        break;                }            }            //Main Shop page layout            elseif(is_shop() || is_product_category() || is_product_tag()) {                if($main_shop_layout == 's3' || $main_shop_layout == 's2'){                    add_filter('loop_shop_columns', 'arcane_loop_columns');                }                switch($main_shop_layout) {                    case 's1': ?>                    <div class="col-12">                        <?php woocommerce_content(); ?>                    <div class="clear"></div>                    <?php    break;                    case 's3':                        echo '<div class="col-8">';                            woocommerce_content();                        echo '</div><!--/col-8-->';                        echo '<div id="sidebar" class="col-4">';                            get_sidebar();                        echo '</div><!--/col-8-->';                        break;                    case 's2':                        echo '<div id="sidebar" class="col-4">';                            get_sidebar();                        echo '</div><!--/col-8-->';                        echo '<div class="col-8">';                            woocommerce_content();                        echo '</div><!--/col-8-->';                        break;                    default:                        woocommerce_content();                        break;                }            }            //regular WooCommerce page layout            else { ?>            <div class="col-12">                <?php woocommerce_content(); ?>            <div class="clear"></div>          <?php  }             ?>			</div>		<div class="clear"></div>		</div>		<div class="clear"></div>	 </div></div><?php get_footer(); ?>